const assert = require('assert')

const randomColor = require('../lib/random').randomColor
const randomNum = require('../lib/random').randomNum
const randomString = require('../lib/random').randomString

describe('test Random:', function () {
    describe('#randomColor()', function () {
        it(`/^#[0-9a-fA-F]$/.test(randomColor()) should return true`, function () {
            assert(/^#[0-9a-fA-F]{6}$/.test(randomColor()))
        })
    })

    describe('#randomNum()', function () {
        it(`10 <= randomNum(10, 100) <= 100 should return true`, function () {
            let num = randomNum(10, 100)
            assert(num <= 100 && num > 10)
        })
        it(`20 <= randomNum(20, 1000) <= 1000 should return true`, function () {
            let num = randomNum(20, 1000)
            assert(num <= 1000 && num > 20)
        })
    })

    describe('#randomString()', function () {
        it(`/^[0-9a-zA-Z]{32}$/.test(randomString()) should return true`, function () {
            assert(/^[0-9a-zA-Z]{32}$/.test(randomString()))
        })

        it(`/^[0-9a-zA-Z]{10}$/.test(randomString(10)) should return true`, function () {
            assert(/^[0-9a-zA-Z]{10}$/.test(randomString(10)))
        })

        it(`/^[0-9a-zA-Z]{20}$/.test(randomString(20)) should return true`, function () {
            assert(/^[0-9a-zA-Z]{20}$/.test(randomString(20)))
        })
    })
})