const assert = require('assert')

const isIdCard = require('../lib/regexp').isIdCard
const isEmail = require('../lib/regexp').isEmail
const isPhoneNum = require('../lib/regexp').isPhoneNum
const isUrl = require('../lib/regexp').isUrl
    
describe('test RegExp:', function () {
    describe('#isIdCard()', function () {
        it(`isIdCard('110102200001011618') should return true`, function () {
            assert(isIdCard('110102200001011618'))
        })

        it(`isIdCard('42060619931208155x') should return true`, function () {
            assert(isIdCard('42060619931208155x'))
        })
    })

    describe('#isEmail()', function () {
        it(`isEmail('35463733@qq.com') should return true`, function () {
            assert(isEmail('35463733@qq.com'))
        })

        it(`isEmail('123456@126.com') should return true`, function () {
            assert(isEmail('123456@126.com'))
        })
    })

    describe('#isPhoneNum()', function () {
        it(`isPhoneNum('13877777666') should return true`, function () {
            assert(isPhoneNum('13877777666'))
        })

        it(`isPhoneNum('18976545671') should return true`, function () {
            assert(isPhoneNum('18976545671'))
        })
    })

    describe('#isUrl()', function () {
        it(`isUrl('https://www.baidu.com') should return true`, function () {
            assert(isUrl('https://www.baidu.com'))
        })

        it(`isUrl('https://space.bilibili.com/122879?spm_id_from=333.338.v_upinfo.2#/video') should return true`, function () {
            assert(isUrl('https://space.bilibili.com/122879?spm_id_from=333.338.v_upinfo.2#/video'))
        })
    })
})